Example of use:

public class MyClass : MonoBehaviour
{
    HapticController glove;

    void Start ()
    {
        int status = NDController.GetController (out glove);

        if (status == 0) {
            glove.PlayPulse (Actuator.ACT_THUMB, 0.5f, 300); //Pulse at thumb tip, intensity 0.5, duration 300 ms.            
        }
    }
}

